import axios from 'axios';
import { EnergyPrice } from '../types/energy';

const API_URL = 'http://localhost:8000/api';

export const getLatestEnergyData = async (): Promise<EnergyPrice[]> => {
  try {
    const response = await axios.get<EnergyPrice[]>(`${API_URL}/latest-energy-data/`);
    return response.data;
  } catch (error) {
    console.error('Error fetching energy data:', error);
    throw error;
  }
};

export const getEnergyPrices = async (): Promise<EnergyPrice[]> => {
  try {
    const response = await axios.get<EnergyPrice[]>(`${API_URL}/energy-prices/`);
    return response.data;
  } catch (error) {
    console.error('Error fetching energy prices:', error);
    throw error;
  }
};
